package ru.jmentor.transfer;import ru.jmentor.model.User;public class UserDto {    private String userName;    private String userRole;    public UserDto(String userName, String userRole) {        this.userName = userName;        this.userRole = userRole;    }    public UserDto() { }    public String getUserName() { return userName; }    public void setUserName(String userName) { this.userName = userName; }    public String getUserRole() { return userRole; }    public void setUserRole(String userRole) { this.userRole = userRole; }    public static UserDto from(User user){        UserDto userDto = new UserDto();        userDto.setUserName(user.getUserName());        userDto.setUserRole(user.getRole().toString());        return userDto;    }}